// auth.module.ts

import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AuthComponent } from './auth.component';

@NgModule({
    declarations: [
        AuthComponent
        // AuthComponent should not be declared here
    ],
    imports: [
        CommonModule,

        // Other modules that are needed for the auth module
    ],
    exports: [
        // Export AuthComponent if you need to use it in other modules
        AuthComponent,
    ],
})
export class AuthModule { }
