import { Component } from '@angular/core';
import {Router} from "@angular/router";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.sass']
})
export class AppComponent {
  title = 'scoreboardpro';
  constructor(private router: Router) {}

  isAuthRouteActivated(): boolean {
    return this.router.url === '/auth';
  }
}
<div class="flex flex-col text-textBlack h-screen overflow-hidden">
    <!-- Sticky Header -->
    <div class="w-full z-10 fixed top-0">
        <app-header></app-header>
    </div>

    <!-- Main Content Area with Sidebar -->
    <div class="flex mt-20">
        <!-- Sticky Sidebar -->
        <div class="fixed w-60 h-screen overflow-y-auto">
            <app-sidenav></app-sidenav>
        </div>

        <!-- Router Outlet Content -->
        <div class=" ml-60 py-4 px-8 bg-cardWhite overflow-y-auto w-full h-screen">
            <router-outlet class=""></router-outlet>

        </div>
    </div>
</div>
<app-auth *ngIf="isAuthRouteActivated()"></app-auth>
