import { Component } from '@angular/core';

@Component({
  selector: 'app-search-bar',
  templateUrl: './search-bar.component.html',
  styleUrls: ['./search-bar.component.sass']
})
export class SearchBarComponent {

}
<div class="flex py-4 px-4 bg-white rounded-md thin-border-gray">
    <div class="search-bar rounded-md w-full   text-textBlack flex gap-2">
        <span class="">Search player by name</span>
        <div class="flex bg-primaryBorderGrey justify-center items-center rounded-md w-full">
            <i class="fa-solid fa-magnifying-glass px-2 text-gray-400 text-center "></i>
            <input type="text"
                   class="py-1.5 px-2 outline-0 bg-primaryBorderGrey w-5/6 rounded-md font-normal placeholder:font-normal text-primaryGray"
                   placeholder="Search...">
        </div>
    </div>
</div>
