import {Component, inject, OnInit} from '@angular/core';
import {PlayerService} from "../../services/player.service";
import {Player} from "../../models/player.model";

@Component({
  selector: 'app-players',
  templateUrl: './players.component.html',
  styleUrls: ['./players.component.sass']
})
export class PlayersComponent implements  OnInit{
  private playerService = inject(PlayerService)
  selectedPlayer:string = '';
  players!: Player;
  ngOnInit() {
    // this.playerService.getSelectedPlayer().subscribe((playerName)=> {
    //   this.selectedPlayer = playerName
    // })
    this.playerService.selectedPlayer$.subscribe((playerName)=>{
      this.selectedPlayer = playerName;
      if(playerName) {
        this.playerService.getPlayersDetails(playerName).subscribe(
            (data)=>{
              this.players = data;
              console.log(this.players)
            },
            (error) => {
              console.error(error)
            }
        )
      }
    })
  }
}
<div class="p-4">
    <app-search-bar></app-search-bar>
    <app-player *ngFor="let player of players" [playerInfo]="player"></app-player>
    <p *ngIf="selectedPlayer">{{ selectedPlayer }}</p>
</div>
